import React, {Component} from 'react';
import { connect } from 'react-redux'
import { SubmissionError } from 'redux-form';
import { loginAPI } from '../../actions/auth'
import { Button,Icon, Layout, Table, Card } from 'element-react';
import {getLostItemListAPI} from '../../actions/itemlist'
import 'element-theme-default';

class LostItems extends Component {

  handleSubmit = (data) => {
    console.log(data);  

    return this.props.login(data)
      .then((res) => {
        console.log(res);
        this.props.history.push('/admin');        
      }, (err) => {
        console.log(err.response);
        throw new SubmissionError({
          _error: 'Login failed! Email Or Password not correct'
        })
      });
  }

  handleDetailBtn = (data)=>{
    console.log(data)
  }


  constructor(props) {
    super(props);
  
    this.state = {
      columns: [
        {
          label: "Date",
          prop: "date",
          width: 180,
          render: function(data){
            console.log(data);
            return (
            <span>
              <Icon name="time"/>
              <span style={{marginLeft: '10px'}} onClick ={this.handleDetailBtn(data)}>{data.date}</span>
            </span>)
          }
        },
        {
          label: "Name",
          prop: "name",
          width: 180
        },
        {
            label: "Category",
            prop: "category",
            width: 180
        },
        {
            label: "Region",
            prop: "region",

        },
        
        {
          label: "...",
          width: 200,
          render: function() {
            return (
              <span>
                <Button type="primary" size="normal" className='my-table-button' icon="star-on">Detail</Button>
              </span>
            )
          }
        }
      ],
      data: []
    }
  }

  componentDidMount() {
    this.props.getLostItemList()
      .then(response => {
        console.log(response.data);
        this.setState({ data: response.data });
      })
      .catch(function (error) {
        console.log(error);
      })
  }
  
  render() {
    return (
      	<Layout.Row>
        	<Layout.Col span="16"  offset="4"><div className="grid-content bg-purple-dark" >
       			<Card className="box-card, TABLE_CARD">
          		<h1>LOST ITEMS LIST</h1>
          		<hr/>
                  <Table
                        style={{width: '100%'}}
                        columns={this.state.columns}
                        data={this.state.data}
                        border={true}
                        height={500}
                        highlightCurrentRow={true}
                        onCurrentChange={item=>{console.log(item)}}
                    />
    				</Card></div>
    			</Layout.Col>
      	</Layout.Row>
    )
  }
}
  
const reduxDispatch = (dispatch) => ({
  getLostItemList: () => dispatch(getLostItemListAPI())
});

export default connect(null, reduxDispatch)(LostItems);
