import React, {Component, Fragment} from 'react';
import { Container, Col, Row, Card } from 'react-bootstrap';
import { connect } from 'react-redux'
import { SubmissionError } from 'redux-form';
import { loginAPI } from '../../actions/auth'

class PostLostItem extends Component {

  handleSubmit = (data) => {
    console.log(data);  

    return this.props.login(data)
      .then((res) => {
        console.log(res);
        this.props.history.push('/admin');        
      }, (err) => {
        console.log(err.response);
        throw new SubmissionError({
          _error: 'Login failed! Email Or Password not correct'
        })
      });
  }

  render() {
    // const {handleSubmit} = this;
    const PostFoundItem = (
    <Card className="HOME_CARD2">
        <div class="card-body">
            <h1 class="card-title">REPORT FOUND ITEM</h1>
            <p class="card-text">Welcome here! You can report your found item.</p>
            <a href="/found" class="btn btn-dark my-post-button">CLICK HERE</a>
        </div>
    </Card>);
    return (
      <Fragment>
        <Container className="mt-8">
          <Row>
            <Col md={6} className="mx-auto col-md-8">
              <hr/>
              {PostFoundItem}
            </Col>
          </Row>
        </Container>
      </Fragment>
    );
  }

}

const reduxDispatch = (dispatch) => ({
  login: (data) => dispatch(loginAPI(data))
});

export default connect(null, reduxDispatch)(PostLostItem);
