import React, {Component, Fragment} from 'react';
// import { Container, Col, Row, Card } from 'react-bootstrap';
import { connect } from 'react-redux'
import { SubmissionError } from 'redux-form';
import { loginAPI } from '../../actions/auth'
import { Button,Form, Input,Select,Layout,DatePicker,TimePicker,Card, Table,Checkbox,Upload } from 'element-react';
import 'element-theme-default';

class FoundItemDetails extends Component {

  handleSubmit = (data) => {
    console.log(data);  

    return this.props.login(data)
      .then((res) => {
        console.log(res);
        this.props.history.push('/admin');        
      }, (err) => {
        console.log(err.response);
        throw new SubmissionError({
          _error: 'Login failed! Email Or Password not correct'
        })
      });
  }
  constructor(props) {
    super(props);
  
    this.state = {
      name:'',
      data:[]
    }
  }
  
  render() {
    return (

      <div>
        <br/>
        <hr/>
      	<Layout.Row>
        	<Layout.Col span="6"  offset="3">
            <div className="grid-content bg-purple-dark" >
       			<Card className="box-card, DETAIL_TABLE">
              <img scr = "" width={100} height={100}></img>
    				</Card></div>
    			</Layout.Col>
          <Layout.Col span="12"  offset="1">
            <div className="grid-content bg-purple-dark" >
       			<Card className="box-card, DETAIL_TABLE">
             <Layout.Row className="DETAIL_CARD_ITEM">
              <Layout.Col span="8">
                Name:
              </Layout.Col>
              <Layout.Col span="16">
                {this.state.name}
              </Layout.Col>
              </Layout.Row>
              <Layout.Row className="DETAIL_CARD_ITEM">
              <Layout.Col span="8">
                Email:
              </Layout.Col>
              <Layout.Col span="16">
                {this.state.name}
              </Layout.Col>
              </Layout.Row>
              <Layout.Row className="DETAIL_CARD_ITEM">
              <Layout.Col span="8">
                Region:
              </Layout.Col>
              <Layout.Col span="16">
                {this.state.name}
              </Layout.Col>
              </Layout.Row>
              <Layout.Row className="DETAIL_CARD_ITEM">
              <Layout.Col span="8">
                Category:
              </Layout.Col>
              <Layout.Col span="16">
                {this.state.name}
              </Layout.Col>
              </Layout.Row>
              <Layout.Row className="DETAIL_CARD_ITEM">
              <Layout.Col span="8">
                Description:
              </Layout.Col>
              <Layout.Col span="16">
                {this.state.name}
              </Layout.Col>
              </Layout.Row>
              <Layout.Row className="DETAIL_CARD_ITEM">
              <Layout.Col span="8">
                Phone:
              </Layout.Col>
              <Layout.Col span="16">
                {this.state.name}
              </Layout.Col>
              </Layout.Row>
              <Layout.Row>
                <Button className = "my-table-button item-detail" type='info' size='large' icon="share"> CONTACT</Button>
              </Layout.Row>
    				</Card></div>
    			</Layout.Col>
      	</Layout.Row>
    	</div>
    )
  }    
}

const reduxDispatch = (dispatch) => ({
  login: (data) => dispatch(loginAPI(data))
});

export default connect(null, reduxDispatch)(FoundItemDetails);
