import React, {Component, Fragment} from 'react';
// import { Container, Col, Row, Card } from 'react-bootstrap';
import { connect } from 'react-redux'
import { SubmissionError } from 'redux-form';
import { loginAPI } from '../../actions/auth'
import { Button,Icon, Tag,  Table } from 'element-react';
import 'element-theme-default';

class FoundItemDetails extends Component {

  handleSubmit = (data) => {
    console.log(data);  

    return this.props.login(data)
      .then((res) => {
        console.log(res);
        this.props.history.push('/admin');        
      }, (err) => {
        console.log(err.response);
        throw new SubmissionError({
          _error: 'Login failed! Email Or Password not correct'
        })
      });
  }

  constructor(props) {
    super(props);
  
    this.state = {
      columns: [
        {
          type: 'index'
        },
        {
          label: "Date",
          prop: "date",
          width: 180,
          render: function(data){
            return (
            <span>
              <Icon name="time"/>
              <span style={{marginLeft: '10px'}}>{data.date}</span>
            </span>)
          }
        },
        {
          label: "Name",
          prop: "name",
          width: 180,
        //   render: function(data){
        //     return <Tag>{data.name}</Tag>
        //   }
        },
        {
            label: "Name",
            prop: "name",
            width: 180,
            render: function(data){
              return <Tag>{data.name}</Tag>
            }
        },
        {
            label: "Name",
            prop: "name",
            width: 180,
            render: function(data){
              return <Tag>{data.name}</Tag>
            }
        },
        {
          label: "Operations",
          render: function() {
            return (
              <span>
               <Button plain={true} type="info" size="small">Edit</Button>
               <Button type="danger" size="small">Delete</Button>
              </span>
            )
          }
        }
      ],
      data: [{
        date: '2016-05-03',
        name: 'Tom',
        address: 'No. 189, Grove St, Los Angeles'
      }, {
        date: '2016-05-02',
        name: 'Tom',
        address: 'No. 189, Grove St, Los Angeles'
      }, {
        date: '2016-05-04',
        name: 'Tom',
        address: 'No. 189, Grove St, Los Angeles'
      }, {
        date: '2016-05-01',
        name: 'Tom',
        address: 'No. 189, Grove St, Los Angeles'
      }]
    }
  }
  
  render() {
    return (
      <Table
        style={{width: '100%'}}
        columns={this.state.columns}
        data={this.state.data}
        border={true}
        highlightCurrentRow={true}
        onCurrentChange={item=>{console.log(item)}}
      />
    )
  }
}
  
const reduxDispatch = (dispatch) => ({
  login: (data) => dispatch(loginAPI(data))
});

export default connect(null, reduxDispatch)(FoundItemDetails);
