import React, {Component, Fragment} from 'react';
// import { Container, Col, Row, Card } from 'react-bootstrap';
import { connect } from 'react-redux'
import { SubmissionError } from 'redux-form';
import { loginAPI } from '../../actions/auth'

import { Button,Form, Input,Select,Layout,DatePicker,TimePicker,Card, Switch,Checkbox,Upload } from 'element-react';
class PostLostItem extends Component {

  handleSubmit = (data) => {
    console.log(data);  

    return this.props.login(data)
      .then((res) => {
        console.log(res);
        this.props.history.push('/founddetail');        
      }, (err) => {
        console.log(err.response);
        throw new SubmissionError({
          _error: 'Login failed! Email Or Password not correct'
        })
      });
  }
  constructor(props) {
    super(props);
  
    this.state = {
      form: {
        name: '',
        region: '',
        category: '',
        date1: null,
        date2: null,
        desc: ''
      },
	  rules: {
		name: [
		  { required: true, message: 'Please enter item name.', trigger: 'blur' }
		],
		region: [
		  { required: true, message: 'Please enter region.', trigger: 'blur' }
		],
		date1: [
		  { type: 'date', required: true, message: 'Please select date.', trigger: 'change' }
		],
		date2: [
		  { type: 'date', required: true, message: 'Please select time', trigger: 'change' }
		],
		category: [
			{ required: true, message: 'Please select category.', trigger: 'change' }
		  ],
		desc: [
		  { required: true, message: 'Please enter description', trigger: 'blur' }
		]
	  }
    };
  }
  
  onSubmit(e) {
    e.preventDefault();
  }
  
  onChange(key, value) {
	this.setState({
		form: Object.assign({}, this.state.form, { [key]: value })
	  });
  }
  render() {
    // const {handleSubmit} = this;
		const leftCard = (
			<Layout.Col span="12">
				<Form className="en-US" model={this.state.form} rules={this.state.rules} labelWidth="120" onSubmit={this.onSubmit.bind(this)}>
					<Form.Item label="Item name">
						<Input value={this.state.form.name} onChange={this.onChange.bind(this, 'name')}></Input>
					</Form.Item>
					<Form.Item label="Category">
						<Select value={this.state.form.region} placeholder="Please select your category">
							<Select.Option label="Cate 1" value="A"></Select.Option>
							<Select.Option label="Cate 2" value="B"></Select.Option>
							<Select.Option label="Cate 3" value="C"></Select.Option>
							<Select.Option label="Cate 4" value="D"></Select.Option>
						</Select>
					</Form.Item>
					<Form.Item label="Region">
						<Input value={this.state.form.region} onChange={this.onChange.bind(this, 'region')}></Input>
					</Form.Item>
					<Form.Item label="Lost Date">
					<Layout.Col span="11">
						<Form.Item prop="date1" labelWidth="0px">
							<DatePicker
								value={this.state.form.date1}
								placeholder="Pick a date"
								onChange={this.onChange.bind(this, 'date1')}
							/>
						</Form.Item>
					</Layout.Col>
					<Layout.Col className="line" span="2">-</Layout.Col>
							<Layout.Col span="11">
								<Form.Item prop="date2" labelWidth="0px">
									<TimePicker
										value={this.state.form.date2}
										selectableRange="00:00:00 - 23:59:59"
										placeholder="Pick a time"
										onChange={this.onChange.bind(this, 'date2')}
									/>
								</Form.Item>
							</Layout.Col>
						</Form.Item>
						<Form.Item label="Description">
							<Input type="textarea" value={this.state.form.desc} onChange={this.onChange.bind(this, 'desc')}></Input>
						</Form.Item>
						<Form.Item>
							<Button type="primary" nativeType="submit" icon ="caret-right">SUBMIT</Button>
							<Button icon="search" type="primary">Search</Button>
						</Form.Item>
					</Form>
				</Layout.Col> );
	const rightCard = (
		<Layout.Col span="6" offset="2">
      <Upload
				className="upload-demo"
				drag
				action="//jsonplaceholder.typicode.com/posts/"
				// multiple
				tip={<div className="el-upload__tip">jpg/png files with a size less than 2mb</div>}>
				<i className="el-icon-upload"></i>
				<div className="el-upload__text">Drop file here or<em>Click to upload</em></div>
    	</Upload>
   </Layout.Col>
	);

    return (
      <div>
        <br/>
        <hr/>
      	<Layout.Row>
        	<Layout.Col span="18"  offset="3"><div className="grid-content bg-purple-dark" >
       			<Card className="box-card, HOME_CARD1">
          		<h3>Report Lost Item</h3>
          		<hr/>
							<Layout.Row>
								{ leftCard }  
								{ rightCard }
							</Layout.Row>
    				</Card></div>
    			</Layout.Col>
      	</Layout.Row>
    	</div>
    );
  }

  }



const reduxDispatch = (dispatch) => ({
  login: (data) => dispatch(loginAPI(data))
});

export default connect(null, reduxDispatch)(PostLostItem);
