import React, {Component, Fragment} from 'react';
// import { Container, Col, Row, Card } from 'react-bootstrap';
import { connect } from 'react-redux'
import { SubmissionError } from 'redux-form';
import { postfoundAPI } from '../../actions/post'
 import { Button,Form, Input,Select,Layout,DatePicker,TimePicker,Card, Switch,Checkbox,Upload } from 'element-react';
 import 'element-theme-default';

class PostFoundItem extends Component {

  handleSubmit = (state) => {
	var data=state;
    console.log(data);  

    return this.props.post(data)
      .then((res) => {
        console.log(res);
        this.props.history.push('/lostdetail');        
      }, (err) => {
        console.log(err.response);
        throw new SubmissionError({
          _error: 'Post failed!'
        })
      });
  }
//   constructor(props) {
//     super(props);
  
    state = {
      form: {
        name: '',
        region: '',
        category: '',
        date1: null,
        date2: null,
        delivery: false,
        type: [],
        resource: '',
        desc: ''
      }
    };
//   }
  
  onSubmit(e) {
    e.preventDefault();
  }
  
  
  onChange(key, value) {

    this.state.form[key] = value;
    this.forceUpdate();
  }
  render() {
     const {handleSubmit} = this;
		const leftCard = (
			<Layout.Col span="12">
				<Form className="en-US" model={this.state.form} labelWidth="120" onSubmit={this.onSubmit.bind(this)}>
					<Form.Item label="Item name">
						<Input value={this.state.form.name} onChange={this.onChange.bind(this, 'name')}></Input>
					</Form.Item>
					<Form.Item label="Category">
						<Select value={this.state.form.category} placeholder="Please select category">
							<Select.Option label="wallet" value="wallet"></Select.Option>
							<Select.Option label="watch" value="watch"></Select.Option>
						</Select>
					</Form.Item>
					<Form.Item label="Region">
						<Input value={this.state.form.region} onChange={this.onChange.bind(this, 'region')}></Input>
					</Form.Item>
					<Form.Item label="Found Date">
					<Layout.Col span="11">
						<Form.Item prop="date1" labelWidth="0px">
							<DatePicker
								value={this.state.form.date1}
								placeholder="Pick a date"
								onChange={this.onChange.bind(this, 'date1')}
							/>
						</Form.Item>
					</Layout.Col>
					<Layout.Col className="line" span="2">-</Layout.Col>
							<Layout.Col span="11">
								<Form.Item prop="date2" labelWidth="0px">
									<TimePicker
										value={this.state.form.date2}
										selectableRange="18:30:00 - 20:30:00"
										placeholder="Pick a time"
										onChange={this.onChange.bind(this, 'date2')}
									/>
								</Form.Item>
							</Layout.Col>
						</Form.Item>
						<Form.Item label="Description">
							<Input type="textarea" value={this.state.form.desc} onChange={this.onChange.bind(this, 'desc')}></Input>
						</Form.Item>
						<Form.Item>
							<Button type="success" nativeType="submit" icon="caret-right">SUBMIT</Button>
							<Button icon="search" type="success" >Search</Button>
						</Form.Item>
					</Form>
				</Layout.Col> );
	const rightCard = (
		<Layout.Col span="6" offset="2">
      <Upload
				className="upload-demo"
				drag
				action="//jsonplaceholder.typicode.com/posts/"
				// multiple
				tip={<div className="el-upload__tip">jpg/png files with a size less than 2mb</div>}>
				<i className="el-icon-upload"></i>
				<div className="el-upload__text">Drop file here or<em>click to upload</em></div>
    	</Upload>
   </Layout.Col>
	);

    return (
      <div>
        <br/>
        <hr/>
      	<Layout.Row>
        	<Layout.Col span="18"  offset="3"><div className="grid-content bg-purple-dark" >
       			<Card className="box-card, HOME_CARD2">
          		<h3>Report Found Item</h3>
          		<hr/>
							<Layout.Row>
								{	leftCard }  
								{ rightCard }
							</Layout.Row>
    				</Card></div>
    			</Layout.Col>
      	</Layout.Row>
    	</div>
    );
  }
}

const reduxDispatch = (dispatch) => ({
  post: (data) => dispatch(postfoundAPI(data))
});

export default connect(null, reduxDispatch)(PostFoundItem);
