import React, {Component, Fragment} from 'react';
// import { Container, Col, Row, Card } from 'react-bootstrap';
import { connect } from 'react-redux'
import { SubmissionError } from 'redux-form';
import { loginAPI } from '../../actions/auth'
import { Button,Dialog, Input,Select,Layout,DatePicker,TimePicker,Card, Table,Checkbox,Upload } from 'element-react';
import 'element-theme-default';

class LostItemDetails extends Component {

  handleSubmit = (data) => {
    console.log(data);  

    return this.props.login(data)
      .then((res) => {
        console.log(res);
        this.props.history.push('/admin');        
      }, (err) => {
        console.log(err.response);
        throw new SubmissionError({
          _error: 'Login failed! Email Or Password not correct'
        })
      });
  }
  constructor(props) {
    super(props);
    this.state = {
      name:'123123123',
      data:[],
      dialogVisible: false
    }
  }
  
  render() {
    return (
      <div>
        <Button type="text" onClick={ () => this.setState({ dialogVisible: true }) }>Click to open the Dialog</Button>
        <Dialog
          title="Tips"
          size="tiny"
          visible={ this.state.dialogVisible }
          onCancel={ () => this.setState({ dialogVisible: false }) }
          lockScroll={ false }
        >
          <Dialog.Body>
            <span>This is a message</span>
          </Dialog.Body>
          <Dialog.Footer className="dialog-footer">
            <Button onClick={ () => this.setState({ dialogVisible: false }) }>Cancel</Button>
            <Button type="primary" onClick={ () => this.setState({ dialogVisible: false }) }>Confirm</Button>
          </Dialog.Footer>
        </Dialog>
      </div>
    )
  }
}

const reduxDispatch = (dispatch) => ({
  login: (data) => dispatch(loginAPI(data))
});

export default connect(null, reduxDispatch)(LostItemDetails);
