import React, {Component, Fragment} from 'react';
import { Container, Col, Row, Card } from 'react-bootstrap';
import { connect } from 'react-redux'
import { SubmissionError } from 'redux-form';
import { loginAPI } from '../../actions/auth'
 import { Button,Form, Input,Select,Layout,DatePicker,TimePicker,Radio, Switch,Checkbox,Upload } from 'element-react';

 import 'element-theme-default';

class PostFoundItem extends Component {

  handleSubmit = (data) => {
    console.log(data);  

    return this.props.login(data)
      .then((res) => {
        console.log(res);
        this.props.history.push('/admin');        
      }, (err) => {
        console.log(err.response);
        throw new SubmissionError({
          _error: 'Login failed! Email Or Password not correct'
        })
      });
  }
  constructor(props) {
    super(props);
  
    this.state = {
      form: {
        name: '',
        region: '',
        date1: null,
        date2: null,
        delivery: false,
        type: [],
        resource: '',
        desc: ''
      }
    };
  }
  
  onSubmit(e) {
    e.preventDefault();
  }
  
  onChange(key, value) {
    this.state.form[key] = value;
    this.forceUpdate();
  }
  render() {
    // const {handleSubmit} = this;
    const PostLostItem = (
    <Card className="HOME_CARD1">
        <div class="card-body">
            <h1 class="card-title">REPORT LOST ITEM</h1>
            <p class="card-text">Welcome here! You can report your lost item.</p>
            <a href="/lost" class="btn btn-dark my-post-button" >CLICK HERE</a>
        </div>
    </Card>);

    return (
      <div>
        <br/>
        {/* <Card className="box-card">

    </Card> */}
        <hr/>
      <Layout.Row>
        <Layout.Col span="12"  offset="6"><div className="grid-content bg-purple-dark" >
        <Card className="box-card, HOME_CARD1">
          <h3>Report Found Item</h3>
          <hr/>
          <Layout.Row><Layout.Col span="12">
              <Form className="en-US" model={this.state.form} labelWidth="120" onSubmit={this.onSubmit.bind(this)}>
      <Form.Item label="Activity name">
        <Input value={this.state.form.name} onChange={this.onChange.bind(this, 'name')}></Input>
      </Form.Item>
      <Form.Item label="Category">
        <Select value={this.state.form.region} placeholder="Please select your zone">
          <Select.Option label="Zone 1" value="shanghai"></Select.Option>
          <Select.Option label="Zone 2" value="beijing"></Select.Option>
        </Select>
      </Form.Item>
      <Form.Item label="Found Date">
        <Layout.Col span="11">
          <Form.Item prop="date1" labelWidth="0px">
            <DatePicker
              value={this.state.form.date1}
              placeholder="Pick a date"
              onChange={this.onChange.bind(this, 'date1')}
            />
          </Form.Item>
        </Layout.Col>
        <Layout.Col className="line" span="2">-</Layout.Col>
        <Layout.Col span="11">
          <Form.Item prop="date2" labelWidth="0px">
            <TimePicker
              value={this.state.form.date2}
              selectableRange="18:30:00 - 20:30:00"
              placeholder="Pick a time"
              onChange={this.onChange.bind(this, 'date2')}
            />
          </Form.Item>
        </Layout.Col>
       </Form.Item>
      <Form.Item label="Description">
        <Input type="textarea" value={this.state.form.desc} onChange={this.onChange.bind(this, 'desc')}></Input>
      </Form.Item>
         
      <Form.Item>
        <Button type="primary" nativeType="submit">Post</Button>
        <Button>Cancel</Button>
      </Form.Item>
    </Form></Layout.Col>    <Layout.Col span="6" offset="6">
      <Upload
      className="upload-demo"
      drag
      action="//jsonplaceholder.typicode.com/posts/"
      multiple
      tip={<div className="el-upload__tip">jpg/png files with a size less than 2mb</div>}
    >
      <i className="el-icon-upload"></i>
      <div className="el-upload__text">Drop file here or<em>click to upload</em></div>
    </Upload>
   </Layout.Col>
   </Layout.Row>
    </Card></div>
    </Layout.Col>

      </Layout.Row>
    </div>
    );
  }

}

const reduxDispatch = (dispatch) => ({
  login: (data) => dispatch(loginAPI(data))
});

export default connect(null, reduxDispatch)(PostFoundItem);
