import React, {Component} from 'react';
import { connect } from 'react-redux'
import { postLostItemAPI } from '../../actions/post'
import { Button,Form, Input,Select,Layout,DatePicker,Card,Upload } from 'element-react';
import UploaderComponent from './UploaderComponent'
class PostLostItem extends Component {

  constructor(props) {
    super(props);
  
    this.state = {
      form: {
        name: '',
		email: '',
        region: '',
        category: '',
        date: null,
        description: '',
		phone:'',
		image:'sdfsdfsdfsdfsd'
      },
	  rules: {
		name: [
		  { required: true, message: 'Please enter item name.', trigger: 'blur' }
		],
		email: [
			{ required: true, type:'email', message: 'Please enter email.', trigger: 'blur' }
		  ],
		phone: [
		{ required: true, message: 'Please enter phone.', trigger: 'blur' }
		],
		region: [
		  { required: true, message: 'Please enter region.', trigger: 'blur' }
		],
		date: [
		  { type: 'date', required: true, message: 'Please select date.', trigger: 'change' }
		],
		image: [
		  { type: 'date', trigger: 'change' }
		],
		category: [
			{ required: true, message: 'Please select category.', trigger: 'change' }
		  ],
		  description: [
		  { required: true, message: 'Please enter description', trigger: 'blur' }
		]
	  }
    };
  }

  handleCallback = (childData) =>{
	console.log(childData);
	this.state.form.image = childData;
	return childData;
}

	handleSubmit = async (e) => {
//   handleSubmit(e) {
	e.preventDefault();
    let path = await this.handleCallback();
	console.log(path);
	// this.state.form.append('image',path);
	this.refs.form.validate((valid) => {
	  if (valid) {
		const data = new FormData();
		data.append('image',this.state.form.image);
		 this.props.postLostItem(data);
		// this.props.postLostItem(this.state.form);	
		this.props.history.push('/homepage');       	
	  } else {
		console.log('error submit!!');
		return false;
	  }
	});
  }

  handleSearch(e){
	e.preventDefault();
	this.props.history.push('/lostitems'); 
  }

  handleReset(e) {
	e.preventDefault();
	this.refs.form.resetFields();
  }

  onSubmit(e) {
    e.preventDefault();
  }
  
  onChange(key, value) {
	console.log(this.state.form);
	this.setState({
		form: Object.assign({}, this.state.form, { [key]: value })
	  });

  }

  render() {
		const leftCard = (
			<Layout.Col span="12">
				<Form ref="form" className="en-US" model={this.state.form} rules={this.state.rules} labelWidth="120">
					<Form.Item label="Item name" prop="name" required = {true}>
						<Input value={this.state.form.name} onChange={this.onChange.bind(this, 'name')}></Input>
					</Form.Item>
					<Form.Item label="Email" prop="email" required = {true}>
						<Input value={this.state.form.email} onChange={this.onChange.bind(this, 'email')}></Input>
					</Form.Item>
					<Form.Item label="Phone" prop="phone" required = {true}>
						<Input value={this.state.form.phone} onChange={this.onChange.bind(this, 'phone')}></Input>
					</Form.Item>
					<Form.Item label="Category" prop="category" required = {true}>
						<Select value={this.state.form.category} placeholder="Please select your category" onChange={this.onChange.bind(this, 'category')}>
							<Select.Option label="Cate 1" value="Cate 1"></Select.Option>
							<Select.Option label="Cate 2" value="Cate 2"></Select.Option>
							<Select.Option label="Cate 3" value="Cate 3"></Select.Option>
							<Select.Option label="Cate 4" value="Cate 4"></Select.Option>
						</Select>
					</Form.Item>
					<Form.Item label="Region" prop="region" required = {true}>
						<Input value={this.state.form.region} onChange={this.onChange.bind(this, 'region')}></Input>
					</Form.Item>
					<Form.Item label="Lost Date">
						<Form.Item prop="date" labelWidth="0px" required = {true}>
							<DatePicker className="en-US"
								value={this.state.form.date}
								placeholder="Pick a date"
								onChange={this.onChange.bind(this, 'date')}
							/>
						</Form.Item>
						</Form.Item>
						<Form.Item label="Description" prop="description" required = {true}>
							<Input type="textarea" value={this.state.form.description} onChange={this.onChange.bind(this, 'description')}></Input>
						</Form.Item>
						<Form.Item>
							<Button type="primary" icon ="caret-right" onClick={this.handleSubmit.bind(this)}>SUBMIT</Button>
							<Button icon="search" type="primary" onClick={this.handleSearch.bind(this)}>Search</Button>
						</Form.Item>
					</Form>
				</Layout.Col> );
		const rightCard = (
			<Layout.Col span="6" offset="2" >
				<Upload
					style = "width = 150%;"
					className="upload-demo"
					drag
					action="//jsonplaceholder.typicode.com/posts/"
					// multiple
					tip={<div className="el-upload__tip">jpg/png files with a size less than 2mb</div>}>
					<i className="el-icon-upload"></i>
					<div className="el-upload__text">Drop file here or<em>Click to upload</em></div>
				</Upload>
			</Layout.Col>
		);

		const rightCard2 = (
			<Layout.Col span="6" offset="2" >
				<UploaderComponent parentCallback = {this.handleCallback}/>
			</Layout.Col>
		);

    return (
      <div>
        <br/>
        <hr/>
      	<Layout.Row>
        	<Layout.Col span="18"  offset="3"><div className="grid-content bg-purple-dark" >
       			<Card className="box-card, HOME_CARD1">
				   <h2>REPORT LOST ITEM</h2>
          		<hr/>
							<Layout.Row>
								{ leftCard }  
								{ rightCard2 }
							</Layout.Row>
							{/* <img src="http://localhost:8081/1675721292487-01_baf3add4-32f8-4984-b212-e9b7fe1f386c.jpg" /> */}
    				</Card>
					</div>
    			</Layout.Col>
      	</Layout.Row>
    	</div>
    );
  }

  }



const reduxDispatch = (dispatch) => ({
	postLostItem: (data) => dispatch(postLostItemAPI(data))
});

export default connect(null, reduxDispatch)(PostLostItem);
