import React, {Component, Fragment} from 'react';
// import { Container, Col, Row, Card } from 'react-bootstrap';
import { connect } from 'react-redux'
import { SubmissionError } from 'redux-form';
import { loginAPI } from '../../actions/auth'
import { Button,Form, Input,Select,Layout,DatePicker,TimePicker,Card, Table,Checkbox,Upload } from 'element-react';
import 'element-theme-default';

class FoundItemDetails extends Component {

  handleSubmit = (data) => {
    console.log(data);  

    return this.props.login(data)
      .then((res) => {
        console.log(res);
        this.props.history.push('/admin');        
      }, (err) => {
        console.log(err.response);
        throw new SubmissionError({
          _error: 'Login failed! Email Or Password not correct'
        })
      });
  }
  constructor(props) {
    super(props);
  
    this.state = {
    }
  }
  
  render() {
    return (
      <Layout.Row>
      <Layout.Col span={ 8 } offset={ 0 }>
        <Card bodyStyle={{ padding: 0 }}>
          <img src={this.props.imgSrc} className="image" />
          <div style={{ padding: 14 }}>
            <span>Yummy hamburger</span>
            <div className="bottom clearfix">
              <time className="time">2016-10-21 16:19</time>
              <Button type="text" className="button">Operating button</Button>
            </div>
          </div>
        </Card>
      </Layout.Col>
      <Layout.Col span={ 8 } offset={ 2 }>
        <Card bodyStyle={{ padding: 0 }}>
          <img src={this.props.imgSrc} className="image" />
          <div style={{ padding: 14 }}>
            <span>Yummy hamburger</span>
            <div className="bottom clearfix">
              <time className="time">2016-10-21 16:19</time>
              <Button type="text" className="button">Operating button</Button>
            </div>
          </div>
        </Card>
      </Layout.Col>
    </Layout.Row>
    )
  }    
}

const reduxDispatch = (dispatch) => ({
  login: (data) => dispatch(loginAPI(data))
});

export default connect(null, reduxDispatch)(FoundItemDetails);
