import React, {Component, Fragment} from 'react';
// import { Container, Col, Row, Card } from 'react-bootstrap';
import { connect } from 'react-redux'
import { SubmissionError } from 'redux-form';
import { loginAPI } from '../../actions/auth'
import { Button,Form, Input,Select,Layout,DatePicker,TimePicker,Card, Table,Checkbox,Upload } from 'element-react';
import 'element-theme-default';

class FoundItemDetails extends Component {

  handleSubmit = (data) => {
    console.log(data);  

    return this.props.login(data)
      .then((res) => {
        console.log(res);
        this.props.history.push('/admin');        
      }, (err) => {
        console.log(err.response);
        throw new SubmissionError({
          _error: 'Login failed! Email Or Password not correct'
        })
      });
  }
  constructor(props) {
    super(props);
  
    this.state = {
      columns: [
        {
          label: "Date",
          prop: "date",
          width: 150,
          fixed: 'left'
        },
        {
          label: "Name",
          prop: "name",
          width: 120
        },
        {
          label: "State",
          prop: "state",
          width: 120
        },
        {
          label: "City",
          prop: "city",
          width: 120
        },
        {
          label: "Address",
          prop: "address",
          width: 300
        },
        {
          label: "Operations",
          fixed: 'right',
          width: 120,
          render: ()=>{
            return <span><Button type="text" size="small">Detail</Button><Button type="text" size="small">Edit</Button></span>
          }
        }
      ],
      data: [{
        date: '2016-05-03',
        name: 'Tom',
        state: 'California',
        city: 'Los Angeles',
        address: 'No. 189, Grove St, Los Angeles',
      }, {
        date: '2016-05-02',
        name: 'Tom',
        state: 'California',
        city: 'Los Angeles',
        address: 'No. 189, Grove St, Los Angeles',
      }, {
        date: '2016-05-04',
        name: 'Tom',
        state: 'California',
        city: 'Los Angeles',
        address: 'No. 189, Grove St, Los Angeles',
      }, {
        date: '2016-05-01',
        name: 'Tom',
        state: 'California',
        city: 'Los Angeles',
        address: 'No. 189, Grove St, Los Angeles',
      }, {
        date: '2016-05-08',
        name: 'Tom',
        state: 'California',
        city: 'Los Angeles',
        address: 'No. 189, Grove St, Los Angeles',
      }, {
        date: '2016-05-06',
        name: 'Tom',
        state: 'California',
        city: 'Los Angeles',
        address: 'No. 189, Grove St, Los Angeles',
      }, {
        date: '2016-05-07',
        name: 'Tom',
        state: 'California',
        city: 'Los Angeles',
        address: 'No. 189, Grove St, Los Angeles',
      }]
    }
  }
  
  render() {
    return (

      <div>
        <br/>
        <hr/>
      	<Layout.Row>
        	<Layout.Col span="18"  offset="3"><div className="grid-content bg-purple-dark" >
       			<Card className="box-card, TABLE">
             <Layout.Col span="12"  offset="3">
              <div style="width:100%"></div>
             </Layout.Col>
             <Layout.Col span="12"  offset="3">
             </Layout.Col>
    				</Card></div>
    			</Layout.Col>
      	</Layout.Row>
    	</div>
    )
  }    
}

const reduxDispatch = (dispatch) => ({
  login: (data) => dispatch(loginAPI(data))
});

export default connect(null, reduxDispatch)(FoundItemDetails);
